window.onload=function(){document.getElementById("checkUsername").addEventListener("click",getUsername);}; function getUsername(){var username=document.getElementById("username").value;console.log("Performing function with: "+username);var xhttp=new XMLHttpRequest();xhttp.onreadystatechange=function(){if(xhttp.readyState==4&&xhttp.status==200){var ajaxObject=JSON.parse(xhttp.responseText);setValues(ajaxObject);}};xhttp.open("POST","http://localhost:8085/FrontController/checkUsername.ajax?username="+username,true);xhttp.send();} function setValues(ajaxObject){document.getElementById("message").innerHTML=ajaxObject.message;}